entry.value:
import org.apache.nifi.processor.io.StreamCallback
import java.nio.file.Files
import java.nio.file.Paths

def archivos = [
    "/home/nifi/In/Actividades.csv",
    "/home/nifi/In/Calificaciones.xlsx"
]

def archivosExistentes = []

archivos.each { archivo ->
    if (Files.exists(Paths.get(archivo))) {
        archivosExistentes.add(archivo)
    } else {
        log.error("El archivo ${archivo} no existe.")
    }
}

if (archivosExistentes.size() == archivos.size()) {
    log.info("Todos los archivos existen. Procesando...")
    archivosExistentes.each { archivo ->
        def flowFile = session.create()
        flowFile = session.putAttribute(flowFile, "filename", Paths.get(archivo).getFileName().toString())
        flowFile = session.putAttribute(flowFile, "path", archivo)
        flowFile = session.write(flowFile, { inputStream, outputStream ->
            Files.copy(Paths.get(archivo), outputStream)
        } as StreamCallback)
        session.transfer(flowFile, REL_SUCCESS)
    }
} else {
    log.error("No todos los archivos existen. Enviando a error...")
    def flowFile = session.create()
    flowFile = session.putAttribute(flowFile, "archivos.faltantes", (archivos - archivosExistentes).join(", "))
    session.transfer(flowFile, REL_FAILURE)
}
